    def create_unit_tab(tab, entry_var, from_var, to_var, units, convert_command, result_var):
        ttk.Label(tab, text="Value:").pack()
        entry = ttk.Entry(tab, textvariable=entry_var)
        entry.pack()

        ttk.Label(tab, text="From:").pack()
        from_menu = ttk.Combobox(tab, textvariable=from_var, values=units, state="readonly")
        from_menu.pack()

        ttk.Label(tab, text="To:").pack()
        to_menu = ttk.Combobox(tab, textvariable=to_var, values=units, state="readonly")
        to_menu.pack()

        Button(tab, text="Clear", font=("Arial", 12), command=lambda: clear(entry, from_menu, to_menu, result_var), width=5).pack(side='right')
        Button(tab, text="Convert", command=convert_command).pack(side='left')
        Label(tab, textvariable=result_var, font=("Arial", 12)).pack()
        return entry

    def clear(entry, from_menu, to_menu, result_var):
        entry.delete(0, tk.END)
