def kinetic_energy_input():
    menu = Menu(app)
    app.config(menu=menu)
    calculations_menu = Menu(menu)
    menu.add_cascade(label="File", menu=calculations_menu)
    calculations_menu.add_command(label="Home", command=grant_access)
