    title_frame = LabelFrame(app)
    title_frame.grid(row=0, pady=15)
    title_frame.rowconfigure(0, weight=1)
    title_frame.columnconfigure(0, weight=1)

    main_frame = Frame(app)
    main_frame.grid(row=1, sticky="nsew")
    main_frame.columnconfigure(1, weight=0)
    main_frame.columnconfigure(2, weight=0)
    main_frame.grid_propagate(False)

    title = Label(title_frame, text='KINETIC ENERGY CALCULATION', font=("Helvetica", 35, 'bold'))
    title.grid(sticky='nsew', pady=5, padx=5)

    entry_mass_ke = Entry(main_frame, font=("Helvetica", 25), width=30)
    entry_velocity_ke = Entry(main_frame, font=("Helvetica", 25), width=30)

    Label(main_frame, text="Enter mass (kg):", font=("Helvetica", 25)).grid(row=1, sticky="nsew", pady=20)
    Label(main_frame, text="Enter velocity (m/s):", font=("Helvetica", 25)).grid(row=2, sticky="nsew", pady=20)

    entry_mass_ke.grid(row=1, column=1, columnspan=5, pady=10, ipadx=3, ipady=3)
    entry_velocity_ke.grid(row=2, column=1, columnspan=5, pady=10, ipadx=3, ipady=3)

    result_label = Label(main_frame)
    result_label.grid(row=3, sticky="ne", pady=10,columnspan=3)

    def calculate_kinetic_energy():
        try:
            m = entry_mass_ke.get()
            v = entry_velocity_ke.get()
            ke = 0.5 * float(m) * (float(v) ** 2)
            result_label.config(text=f"Kinetic energy is {ke:.2f} J.", font=("Helvetica", 20, 'bold'))
            return ke
        except ValueError:
            messagebox.showerror("Error", "Only numbers allowed!")

    Button(main_frame, text="Calculate", font=("Helvetica", 18), bg='grey', width=20, height=2, command=calculate_kinetic_energy).grid(row=4, pady=15)

