    v_input.grid(row=2, column=1,pady=10)
    t_input.grid(row=3, column=1,pady=10)

    Label(main_frame, text= "Intial Velocity (m/s):",font =("Helvetica",20)).grid(row=1, column=4,sticky='ne',pady=10)
    Label(main_frame, text= "Final Velocity (m/s):",font =("Helvetica",20)).grid(row=2, column=4,sticky='ne',pady=10)
    Label(main_frame, text= "Displacement (metres):",font =("Helvetica",20)).grid(row=3, column=4,sticky='ne',pady=10)
    u1_input = Entry(main_frame,font =("Helvetica",20),width = 15)
    v1_input = Entry(main_frame,font =("Helvetica",20),width = 15)
    s1_input = Entry(main_frame,font =("Helvetica",20),width = 15)
    u1_input.grid(row=1, column=5,sticky='ne',pady=10)
    v1_input.grid(row=2, column=5,sticky='ne',pady=10)
    s1_input.grid(row=3, column=5,sticky='ne',pady=10)

