    result_label2 = Label(main_frame)
    result_label2.grid(row=6, column=4,sticky='ne',pady=10,columnspan=3)

    def compute():
        def formula1():
            try:
                u = float(u_input.get())
                v = float(v_input.get())
                t = float(t_input.get())

                a = (v-u) / t
                result_label1.config(text= f"Acceleration is {a:.2f}m/sÂ²",font =("Helvetica",25,'bold'))
            except ValueError:
               messagebox.showerror("Error", "Only numbers allowed!")
            except ZeroDivisionError:
               messagebox.showerror("Error", "Division by zero is not allowed!")
        
        def formula2():
            try:
                u1 = float(u1_input.get())
                v1 = float(v1_input.get())
                s1 = float(s1_input.get())

                a = (pow(v1, 2) - pow(u1, 2)) / (2 * s1)
                result_label2.config(text= f"Acceleration is {a:.2f}m/sÂ²",font =("Helvetica",25,'bold'))
            except ValueError:
               messagebox.showerror("Error", "Only numbers allowed!")
            except ZeroDivisionError:
               messagebox.showerror("Error", "Division by zero is not allowed!")
        
        Button(main_frame, text= "Submit",font=("Ariel",18),bg = 'grey',width=20,height=2, command= formula1).grid(row=4,pady =10)
        Button(main_frame, text= "Submit",font=("Ariel",18),bg = 'grey',width=20,height=2,command= formula2).grid(row=4, column=4,pady=10)
    compute()

