    # Main Content Frame (no background colour)
    main_frame = Frame(app, padx=30, pady=30)
    main_frame.grid(row=1, column=0, sticky="nsew")
    main_frame.grid_propagate(False)
    
    # Helper function to create a calculation section (reduced fonts, no colours)
    def create_section(col_start, title, formula, labels, row_start):
        # Formula
        Label(
            main_frame,
            text=formula,
            font=("Helvetica", 16, "bold")
        ).grid(row=row_start, column=col_start, columnspan=3, pady=(0, 8))
        
        # Section description
        Label(
            main_frame,
            text=title,
            font=("Helvetica", 12, "italic")
        ).grid(row=row_start + 1, column=col_start, columnspan=3, pady=(0, 15))
        
        entries = []
        for i, (text, unit) in enumerate(labels):
            Label(
                main_frame,
                text=f"{text} ({unit}):",
                font=("Helvetica", 14),
                anchor="e"
            ).grid(row=row_start + 2 + i, column=col_start, sticky="e", padx=(0, 10), pady=6)
            
            entry = Entry(
                main_frame,
                font=("Helvetica", 14),
                width=20,
                justify="center"
            )
            entry.grid(row=row_start + 2 + i, column=col_start + 1, pady=6, padx=10)
            entries.append(entry)
        
        result = Label(
            main_frame,
            text="",
            font=("Helvetica", 16, "bold")
        )
        result.grid(row=row_start + 6, column=col_start, columnspan=3, pady=15)
        
        return entries, result
    
    # Section 1: s = ut + Â½atÂ²
    entries1, result1 = create_section(
        col_start=0,
        title="Using Initial Velocity, Time & Acceleration",
        formula="s = ut + Â½atÂ²",
        labels=[("Initial velocity", "m/s"), ("Time", "s"), ("Acceleration", "m/sÂ²")],
        row_start=0
    )
    u_entry, t_entry, a_entry = entries1
    
    def calc_formula1():
        try:
            u = float(u_entry.get() or 0)
            t = float(t_entry.get() or 0)
            a = float(a_entry.get() or 0)
            s = u * t + 0.5 * a * t**2
            result1.config(text=f"Displacement = {s:.2f} meters")
        except ValueError:
            messagebox.showerror("Input Error", "Please enter valid numbers only!")
    
    Button(
        main_frame,
        text="Calculate s = ut + Â½atÂ²",
        font=("Helvetica", 12, "bold"),
        width=25,
        height=2,
        command=calc_formula1
    ).grid(row=5, column=0, columnspan=3, pady=10)
    
    # Section 2: s = (u + v)t / 2
    entries2, result2 = create_section(
        col_start=4,
        title="Using Initial & Final Velocity with Time",
        formula="s = (u + v)t / 2",
        labels=[("Initial velocity", "m/s"), ("Final velocity", "m/s"), ("Time", "s")],
        row_start=0
    )
    u2_entry, v2_entry, t2_entry = entries2
    
    def calc_formula2():
        try:
            u = float(u2_entry.get() or 0)
            v = float(v2_entry.get() or 0)
            t = float(t2_entry.get() or 0)
            s = (u + v) * t / 2
            result2.config(text=f"Displacement = {s:.2f} meters")
        except ValueError:
            messagebox.showerror("Input Error", "Please enter valid numbers only!")
    
    Button(
        main_frame,
        text="Calculate s = (u + v)t / 2",
        font=("Helvetica", 12, "bold"),
        width=25,
        height=2,
        command=calc_formula2
    ).grid(row=5, column=4, columnspan=3, pady=10)
    
    # Section 3: s = (vÂ² - uÂ²) / (2a)
    entries3, result3 = create_section(
        col_start=8,
        title="Using Initial & Final Velocity with Acceleration",
        formula="s = (vÂ² - uÂ²) / (2a)",
        labels=[("Initial velocity", "m/s"), ("Final velocity", "m/s"), ("Acceleration", "m/sÂ²")],
        row_start=0
    )
    u3_entry, v3_entry, a3_entry = entries3
    
    def calc_formula3():
        try:
            u = float(u3_entry.get() or 0)
            v = float(v3_entry.get() or 0)
            a = float(a3_entry.get() or 0)
            if a == 0:
                raise ZeroDivisionError
            s = (v**2 - u**2) / (2 * a)
            result3.config(text=f"Displacement = {s:.2f} meters")
        except ZeroDivisionError:
            messagebox.showerror("Error", "Acceleration cannot be zero!")
        except ValueError:
            messagebox.showerror("Input Error", "Please enter valid numbers only!")
    
    Button(
        main_frame,
        text="Calculate s = (vÂ² - uÂ²)/(2a)",
        font=("Helvetica", 12, "bold"),
        width=25,
        height=2,
        command=calc_formula3
    ).grid(row=5, column=8, columnspan=3, pady=10)

