    main_frame.grid(row=1, sticky="nsew")
    main_frame.grid_propagate(False)
    
    Label(main_frame, text="ELECTRICAL POWER CALCULATIONS (OHM'S LAW)".upper(), 
          font=("Helvetica", 30, 'bold')).grid(row=0, column=0, columnspan=7, pady=20)
    
    # Section 1: Power from Voltage & Current (P = V Ã I)
    Label(main_frame, text="P = V Ã I", font=("Helvetica", 16, 'bold')).grid(row=1, column=0, columnspan=3, pady=10)
    Label(main_frame, text="Voltage (V):", font=("Helvetica", 16)).grid(row=2, column=0, sticky="e", padx=10)
    v_entry = Entry(main_frame, width=20)
    v_entry.grid(row=2, column=1, padx=10)
    Label(main_frame, text="Current (A):", font=("Helvetica", 16)).grid(row=3, column=0, sticky="e", padx=10)
    i_entry = Entry(main_frame, width=20)
    i_entry.grid(row=3, column=1, padx=10)
    
    result1 = Label(main_frame, text="", font=("Helvetica", 16, 'bold'), fg="blue")
    result1.grid(row=5, column=0, columnspan=3, pady=10)

    def compute_p_vi():
        try:
            V = float(v_entry.get())
            I = float(i_entry.get())
            P = V * I
            result1.config(text=f"P = {P:.2f} W")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
    
    Button(main_frame, text="Calculate", font=("Helvetica", 14), 
        command=compute_p_vi, width=15).grid(row=4, column=0, columnspan=3, pady=5)
    
    # Section 2: Power from Voltage & Resistance (P = VÂ² / R)
    Label(main_frame, text="P = VÂ² / R", font=("Helvetica", 16, 'bold')).grid(row=1, column=3, columnspan=3, pady=10)
    Label(main_frame, text="Voltage (V):", font=("Helvetica", 16)).grid(row=2, column=3, sticky="e", padx=10)
    v2_entry = Entry(main_frame, width=20)
    v2_entry.grid(row=2, column=4, padx=10)
    Label(main_frame, text="Resistance (Î©):", font=("Helvetica", 16)).grid(row=3, column=3, sticky="e", padx=10)
    r_entry = Entry(main_frame, width=20)
    r_entry.grid(row=3, column=4, padx=10)
    
    result2 = Label(main_frame, text="", font=("Helvetica", 16, 'bold'), fg="blue")
    result2.grid(row=5, column=3, columnspan=3, pady=10)
    
    def compute_p_vr():
        try:
            V = float(v2_entry.get())
            R = float(r_entry.get())
            if R == 0:
                raise ZeroDivisionError("Resistance cannot be zero!")
            P = (V ** 2) / R
            result2.config(text=f"P = {P:.2f} W")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
        except ZeroDivisionError as e:
            messagebox.showerror("Error", str(e))
    
    Button(main_frame, text="Calculate", font=("Helvetica", 14), 
        command=compute_p_vr, width=15).grid(row=4, column=3, columnspan=3, pady=5)
    
    # Section 3: Power from Current & Resistance (P = IÂ² Ã R)
    Label(main_frame, text="P = IÂ² Ã R", font=("Helvetica", 16, 'bold')).grid(row=1, column=6, columnspan=3, pady=10)
    Label(main_frame, text="Current (A):", font=("Helvetica", 16)).grid(row=2, column=6, sticky="e", padx=10)
    i2_entry = Entry(main_frame, width=20)
    i2_entry.grid(row=2, column=7, padx=10)
    Label(main_frame, text="Resistance (Î©):", font=("Helvetica", 16)).grid(row=3, column=6, sticky="e", padx=10)
    r2_entry = Entry(main_frame, width=20)
    r2_entry.grid(row=3, column=7, padx=10)
    
    result3 = Label(main_frame, text="", font=("Helvetica", 16, 'bold'), fg="blue")
    result3.grid(row=5, column=6, columnspan=3, pady=10)
    
    def compute_p_ir():
        try:
            I = float(i2_entry.get())
            R = float(r2_entry.get())
            P = (I ** 2) * R
            result3.config(text=f"P = {P:.2f} W")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
    
    Button(main_frame, text="Calculate", font=("Helvetica", 14), 
        command=compute_p_ir, width=15).grid(row=4, column=6, columnspan=3, pady=5)
    
