        #initial velocity
        try:
            v = float(e1.get())
            a = float(e2.get())
            t = float(e8.get())
            u = v - a *t
            result_label2.config(text= f"Initial Velocity is {u}(m/s)",font =("Helvetica",20,'bold'))
        except ValueError:
            messagebox.showerror("Error", "Only numbers allowed!")
