    main_frame = Frame(app, width=1400, height=800)  # Larger frame for 5 sections
    main_frame.grid(row=1, sticky="nsew")
    main_frame.grid_propagate(False)
    
    Label(main_frame, text="ELECTRICAL CURRENT CALCULATIONS".upper(), 
          font=("Helvetica", 30, 'bold')).grid(row=0, column=0, columnspan=9, pady=20)
    
    # Section 1: I = Q / t
    Label(main_frame, text="I = Q / t", font=("Helvetica", 16, 'bold')).grid(row=1, column=0, columnspan=3, pady=10)
    Label(main_frame, text="Charge Q (C):", font=("Helvetica", 14)).grid(row=2, column=0, sticky="e", padx=10)
    c_entry = Entry(main_frame, width=18)
    c_entry.grid(row=2, column=1, padx=10)
    Label(main_frame, text="Time t (s):", font=("Helvetica", 14)).grid(row=3, column=0, sticky="e", padx=10)
    t_entry = Entry(main_frame, width=18)
    t_entry.grid(row=3, column=1, padx=10)
    
    result1 = Label(main_frame, text="", font=("Helvetica", 14, 'bold'), fg="blue")
    result1.grid(row=5, column=0, columnspan=3, pady=10)
    
    def compute_i_qt():
        try:
            Q = float(c_entry.get())
            t = float(t_entry.get())
            if t == 0:
                raise ZeroDivisionError("Time cannot be zero!")
            I = Q / t
            result1.config(text=f"I = {I:.4f} A")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
        except ZeroDivisionError as e:
            messagebox.showerror("Error", str(e))
    
    Button(main_frame, text="Calculate", font=("Helvetica", 12), 
           command=compute_i_qt, width=30).grid(row=4, column=0, columnspan=3, pady=5)
    
    # Section 2: I = V / R
    Label(main_frame, text="I = V / R", font=("Helvetica", 16, 'bold')).grid(row=1, column=3, columnspan=3, pady=10)
    Label(main_frame, text="Voltage V (V):", font=("Helvetica", 14)).grid(row=2, column=3, sticky="e", padx=10)
    v_entry = Entry(main_frame, width=18)
    v_entry.grid(row=2, column=4, padx=10)
    Label(main_frame, text="Resistance R (Î©):", font=("Helvetica", 14)).grid(row=3, column=3, sticky="e", padx=10)
    r_entry = Entry(main_frame, width=18)
    r_entry.grid(row=3, column=4, padx=10)
    
    result2 = Label(main_frame, text="", font=("Helvetica", 14, 'bold'), fg="blue")
    result2.grid(row=5, column=3, columnspan=3, pady=10)
    
    def compute_i_vr():
        try:
            V = float(v_entry.get())
            R = float(r_entry.get())
            if R == 0:
                raise ZeroDivisionError("Resistance cannot be zero!")
            I = V / R
            result2.config(text=f"I = {I:.4f} A")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
        except ZeroDivisionError as e:
            messagebox.showerror("Error", str(e))
    
    Button(main_frame, text="Calculate", font=("Helvetica", 12), 
           command=compute_i_vr, width=30).grid(row=4, column=3, columnspan=3, pady=5)
    
    # Section 3: I = P / V
    Label(main_frame, text="I = P / V", font=("Helvetica", 16, 'bold')).grid(row=1, column=6, columnspan=3, pady=10)
    Label(main_frame, text="Power P (W):", font=("Helvetica", 14)).grid(row=2, column=6, sticky="e", padx=10)
    p_entry = Entry(main_frame, width=18)
    p_entry.grid(row=2, column=7, padx=10)
    Label(main_frame, text="Voltage V (V):", font=("Helvetica", 14)).grid(row=3, column=6, sticky="e", padx=10)
    v2 = Entry(main_frame, width=18)
    v2.grid(row=3, column=7, padx=10)
    
    result3 = Label(main_frame, text="", font=("Helvetica", 14, 'bold'), fg="blue")
    result3.grid(row=5, column=6, columnspan=3, pady=10)
    
    def compute_i_pv():
        try:
            P = float(p_entry.get())
            V = float(v2.get())
            if V == 0:
                raise ZeroDivisionError("Voltage cannot be zero!")
            I = P / V
            result3.config(text=f"I = {I:.4f} A")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
        except ZeroDivisionError as e:
            messagebox.showerror("Error", str(e))
    
    Button(main_frame, text="Calculate", font=("Helvetica", 12), 
           command=compute_i_pv, width=30).grid(row=4, column=6, columnspan=3, pady=5)
    
    # Section 4: I = n q A v (Drift Velocity)
    Label(main_frame, text="I = n q A v", font=("Helvetica", 16, 'bold')).grid(row=6, column=0, columnspan=4, pady=20)
    Label(main_frame, text="Carrier density n (mâ»Â³):", font=("Helvetica", 14)).grid(row=7, column=0, sticky="e", padx=10)
    n = Entry(main_frame, width=20)
    n.grid(row=7, column=1, padx=10)
    Label(main_frame, text="Cross-section A (mÂ²):", font=("Helvetica", 14)).grid(row=8, column=0, sticky="e", padx=10)
    A = Entry(main_frame, width=20)
    A.grid(row=8, column=1, padx=10)
    Label(main_frame, text="Drift velocity v (m/s):", font=("Helvetica", 14)).grid(row=9, column=0, sticky="e", padx=10)
    v3 = Entry(main_frame, width=20)
    v3.grid(row=9, column=1, padx=10)
    
    result4 = Label(main_frame, text="", font=("Helvetica", 14, 'bold'), fg="blue")
    result4.grid(row=11, column=0, columnspan=4, pady=10)
    
    def compute_i_drift():
        try:
            n1 = float(n.get())
            v = float(v3.get())
            A1 = float(A.get())
            q = 1.6e-19  # electron charge
            I = n1 * q * A1 * v
            result4.config(text=f"I = {I:.4e} A")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
    
    Button(main_frame, text="Calculate Drift Current", font=("Helvetica", 12), 
           command=compute_i_drift, width=30).grid(row=10, column=0, columnspan=4, pady=5)
    
    # Section 5: Displacement Current in Capacitor I = C (dV/dt)
    Label(main_frame, text="I = C (dV/dt)", font=("Helvetica", 16, 'bold')).grid(row=6, column=4, columnspan=5, pady=20)
    Label(main_frame, text="Capacitance C (F):", font=("Helvetica", 14)).grid(row=7, column=4, sticky="e", padx=10)
    c = Entry(main_frame, width=20)
    c.grid(row=7, column=5, padx=10)
    Label(main_frame, text="Voltage change ÎV (V):", font=("Helvetica", 14)).grid(row=8, column=4, sticky="e", padx=10)
    dv = Entry(main_frame, width=20)
    dv.grid(row=8, column=5, padx=10)
    Label(main_frame, text="Time Ît (s):", font=("Helvetica", 14)).grid(row=9, column=4, sticky="e", padx=10)
    dt = Entry(main_frame, width=20)
    dt.grid(row=9, column=5, padx=10)
    
    result5 = Label(main_frame, text="", font=("Helvetica", 14, 'bold'), fg="blue")
    result5.grid(row=11, column=4, columnspan=5, pady=10)
    
    def compute_i_cap():
        try:
            C = float(c.get())
            dV = float(dv.get())
            dT = float(dt.get())
            if dT == 0:
                raise ZeroDivisionError("Time cannot be zero!")
            I = C * (dV / dT)
            result5.config(text=f"I = {I:.4f} A")
        except ValueError:
            messagebox.showerror("Input Error", "Only numbers allowed!")
        except ZeroDivisionError as e:
            messagebox.showerror("Error", str(e))
    
    Button(main_frame, text="Calculate Capacitor Current", font=("Helvetica", 12), 
           command=compute_i_cap, width=30).grid(row=10, column=4, columnspan=5, pady=5)
    
